generated.css:
@import url("https://fonts.googleapis.com/css2?family=Nunito+Sans:wght@300;400;600;700;800;900&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Sacramento&display=swap");
@import url("https://fonts.googleapis.com/css2?family=Gabriela&display=swap");
html {
  scroll-behavior: smooth;
  font-size: 62.5%;
  width: 100%;
}

body {
  font-size: 1.6rem;
  width: 100%;
  overflow-x: hidden;
  position: relative;
  font-family: "Nunito Sans", sans-serif;
}

*,
*::after,
*::before {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

a,
a:link {
  text-decoration: none;
  color: unset;
}

img {
  object-fit: contain;
  -o-object-fit: contain;
}

textarea {
  resize: none;
}

html.sr .load-hidden {
  visibility: hidden;
}

@keyframes loopHorizontal {
  0% {
    transform: translateX(100%);
  }
  100% {
    transform: translateX(-100%);
  }
}
@keyframes rotate {
  0% {
    transform: rotate(360deg);
  }
  100% {
    transform: rotate(-360deg);
  }
}
@keyframes popIn {
  0% {
    scale: 0;
    translate: 100%;
  }
  50% {
    scale: 1.1;
    translate: 0;
  }
  100% {
    scale: 1;
  }
}
.primary-heading {
  font-size: clamp(2rem, 2vw + 0.5rem, 4rem);
  line-height: 1;
  margin: 2rem;
  text-transform: capitalize;
}

.secondary-heading {
  font-size: 2rem;
  line-height: 1;
  margin-block: 1rem;
  text-transform: capitalize;
}

.tertiary-heading {
  font-size: 2rem;
  font-weight: 400;
  line-height: 1;
  margin-block: 1rem;
  text-transform: capitalize;
}

.paragraph {
  margin-block: 1rem;
  line-height: 1.5;
  font-size: 1.5rem;
}

.grid {
  display: grid;
  width: 100%;
}

.col-3 {
  grid-template-columns: repeat(auto-fit, minmax(22rem, 1fr));
  justify-content: space-between;
  gap: 2rem;
}

.card {
  padding: 2rem;
  border-radius: 0.5rem;
}

.split-2 {
  display: flex;
  justify-content: space-between;
  align-items: center;
  flex-wrap: wrap;
  gap: 2rem;
  margin: 2rem auto;
}
.split-2__txt {
  width: 60%;
}
.split-2__img {
  width: 35%;
}
.split-2__img .main-img {
  width: 100%;
}

@media screen and (max-width: 800px) {
  .split-2 {
    flex-direction: column;
    padding: 4rem;
    width: 100%;
  }
  .split-2__txt {
    width: 100%;
  }
  .split-2__img {
    width: 80%;
  }
}
.navbar {
  height: 6rem;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 2rem 1rem;
  margin: 2rem auto;
  border-radius: 1rem;
  background: rgba(217, 217, 217, 0.2);
  border: 1px solid rgba(30, 30, 30, 0.5);
  box-shadow: 0px 0.4rem 2.5rem rgba(0, 0, 0, 0.25);
  z-index: 50;
  position: fixed;
  left: 2rem;
  right: 2rem;
  top: 4rem;
}
.navbar__logo {
  display: inline-block;
  margin-right: auto;
  justify-self: start;
  width: 12%;
}
.navbar .logo {
  width: 100%;
}
.navbar .btn-primary {
  transform-origin: right;
}

.navigation {
  display: flex;
  gap: 1.5rem;
  text-transform: capitalize;
  font-size: 1.5rem;
  margin-inline: 2rem;
}
.navigation__link {
  cursor: pointer;
  transition: all cubic-bezier(0.23, 1, 0.32, 1) 0.2s;
}
.navigation__link:hover {
  color: #ec992f;
}

.search-icon {
  display: inline-block;
  margin-left: 2rem;
  cursor: pointer;
}

.nav-toggle {
  display: none;
  filter: invert(80%);
  margin-left: 2rem;
}

@media screen and (max-width: 800px) {
  .nav-toggle {
    display: inline-block;
  }
  nav .navbar {
    position: relative;
    left: 0;
    right: 0;
  }
  nav .navigation {
    background-color: #1e293b;
    color: #fff;
    flex-direction: column;
    position: absolute;
    top: 6rem;
    right: 0;
    padding: 2rem 4rem;
    border-radius: 1rem;
    z-index: 60;
  }
  nav .navigation__link {
    width: fit-content;
  }
  nav .navbar__logo {
    width: 30%;
  }
}
.btn {
  min-width: max-content;
  padding: 0.5rem 1rem;
  display: inline-block;
  border-radius: 0.5rem;
  border: unset;
  outline: unset;
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}
.btn a {
  color: inherit;
}

.btn-primary {
  font-size: 2rem;
  padding: 1rem 2rem;
  text-transform: capitalize;
  background-color: #ec992f;
  color: #fff;
  transition: all cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s;
  transform-origin: left;
}

.btn-primary:hover {
  background-color: #ec6a20;
  scale: 1.05;
}

.btn-primary:active {
  translate: 1rem;
  scale: 1.05;
}

.btn-secondary {
  font-size: 2rem;
  padding: 0.5rem 1rem;
  text-transform: capitalize;
  background-color: transparent;
  border: 2px solid #ec992f;
  color: #fff;
  transition: all cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s;
}

.btn-secondary:hover {
  border: 2px solid #ec6a20;
}

@media screen and (max-width: 650px) {
  .btn-primary {
    font-size: 1.6rem;
  }
}
.icon {
  height: 2rem;
  aspect-ratio: 1/1;
}

.primary-color {
  color: #ec992f;
}

.primary-bg {
  background-color: #ec992f;
}

.header-bg {
  background-color: rgba(72, 84, 97, 0.8);
  color: #e0dfdf;
}

.hidden {
  display: none !important;
}

.active {
  border-bottom: 2px solid #ec992f;
}

.newsline {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  gap: 6rem;
  padding: 2rem 4rem;
  background-color: #fff;
  height: 5rem;
  margin-bottom: 2rem;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 50;
}
.newsline__txt {
  display: flex;
  align-items: baseline;
  gap: 0.2rem;
}
.newsline__txt--heading {
  text-transform: uppercase;
  font-weight: 400;
}
.newsline__txt--link {
  text-transform: capitalize;
  color: #ec992f !important;
}
.newsline__link--group {
  display: flex;
  gap: 2rem;
}
.newsline__link {
  height: 2rem;
  transition: cubic-bezier(0.6, -0.28, 0.735, 0.045) 0.2s;
}
.newsline__link:hover {
  scale: 1.15;
}

.header {
  background-image: url("/ca2dc1f0470de49dc3723256fc0c277c.png");
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  background-blend-mode: screen;
  background-color: rgb(8, 23, 39);
  height: 100vh;
  height: 100dvh;
  width: 100%;
  top: 4rem;
}
.header__wrapper {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  color: #fff;
  height: 100%;
  background-image: linear-gradient(rgb(40, 49, 59), rgba(72, 84, 97, 0.46));
  padding: 4rem;
}
.header .search-form {
  display: flex;
  align-items: center;
  position: fixed;
  top: 15rem;
  margin-right: 8rem;
  z-index: 20;
}
.header .search-form__label {
  display: flex;
  align-items: center;
  height: 100%;
  background-color: rgba(72, 84, 97, 0.5);
  outline: 2px solid rgb(72, 84, 97);
  border-radius: 0.5rem;
  margin-left: 1rem;
}
.header .search-form__input, .header .search-form__input:focus {
  background-color: transparent;
  border: unset;
  color: #fff;
  outline: unset;
  height: 100%;
  padding: 1rem;
}
.header .search-form__input::placeholder {
  color: #ec992f;
  text-transform: capitalize;
  font-weight: bold;
}
.header .search-form__close {
  display: inline-block;
  color: #fff;
  padding-inline: 1rem;
  cursor: pointer;
}
.header .search-form .toggle-form {
  cursor: pointer;
  filter: invert(80%);
}

.hero {
  display: block;
  margin-block: auto 0;
  width: 100%;
}
.hero__txt {
  width: 50%;
  text-align: center;
  margin-inline: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
}
.hero__txt--heading {
  font-size: clamp(2rem, 4vw + 0.5rem, 4rem);
}
.hero__info {
  display: flex;
  justify-content: space-between;
  margin-top: 8rem;
}
.hero__info--stats {
  display: flex;
  gap: 4rem;
}
.hero .stat {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.hero__logo {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}
.hero__logo .rig-logo {
  height: 4rem;
}
.hero__logo .rig-txt {
  height: 8rem;
  position: absolute;
  animation: rotate 15s linear infinite;
  z-index: 5;
}

@media screen and (max-width: 800px) {
  .hide-nav {
    display: none !important;
    height: 0;
  }
  .header__wrapper {
    padding: 0;
  }
  .newsline {
    height: fit-content;
    padding: 1rem 2rem;
    justify-content: center;
    width: 100%;
  }
  .newsline__link--group {
    display: none;
  }
  .hero__txt {
    width: 80%;
    margin-block: auto 6rem;
  }
  .hero__logo {
    display: none;
  }
  .hero__info {
    margin: 4rem 1rem;
    gap: 4rem;
    flex-direction: column;
    align-items: flex-end;
  }
  .hero__info--stats {
    align-self: center;
  }
  .hero__info .stat__info {
    font-size: 1.4rem;
  }
}
.footer {
  display: flex;
  align-items: center;
  justify-content: space-evenly;
  padding: 6rem;
  background-color: #2f1f09;
  color: #fff;
  position: relative;
}
.footer .display-on-mobile {
  display: none !important;
}
.footer .display-on-desktop {
  display: flex !important;
}
.footer__logo {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
}
.footer__logo .rig-logo {
  height: 4rem;
}
.footer__logo .rig-txt {
  height: 8rem;
  position: absolute;
  animation: rotate 15s linear infinite;
  z-index: 5;
}
.footer__main {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin-inline: 4rem;
  gap: 1rem;
}
.footer__links {
  display: flex;
  align-items: center;
}
.footer .navigation,
.footer .paragraph {
  font-size: 1.2rem;
}
.footer .nav-group {
  display: flex;
  gap: 1rem;
}
.footer__btn-group {
  display: flex;
  align-items: center;
  gap: 4rem;
}
.footer__btn-group .btn {
  padding: 0.5rem 1.5rem;
  font-size: 1.8rem;
}
.footer__socials {
  display: flex;
  gap: 2rem;
}
.footer__socials .icon {
  transition: all ease-in-out 0.2s;
}
.footer__socials .icon:hover {
  scale: 1.15;
}

@media screen and (max-width: 800px) {
  .footer {
    padding: 4rem;
  }
  .footer .display-on-desktop {
    display: none !important;
  }
  .footer .display-on-mobile {
    display: flex !important;
  }
  .footer__main {
    margin: 2rem;
  }
  .footer__logo {
    margin-right: auto;
  }
  .footer__logo .rig-logo {
    height: 2rem;
  }
  .footer__logo .rig-txt {
    height: 4rem;
  }
  .footer__links {
    flex-direction: column-reverse;
  }
  .footer .navigation {
    align-items: flex-start;
    margin-block: 2rem;
    gap: 4rem;
  }
  .footer .nav-group {
    flex-direction: column;
    align-items: flex-start;
  }
}
.about-page {
  background-color: rgba(72, 84, 97, 0.2);
}
.about-page .display-on-mobile {
  display: none !important;
}
.about-page .display-on-desktop {
  display: flex !important;
}
.about-page .header {
  background-image: url("/60de6522431f608c7de1869c9e70c1ad.png");
  max-height: 56rem;
}
.about-page .header__wrapper {
  background: rgba(236, 153, 47, 0.2);
}
.about-page .hero {
  width: 80%;
  margin: auto;
  display: flex;
  align-items: center;
  justify-content: center;
}
.about-page .about__logo {
  width: 100%;
}
.about-page .main {
  margin-block: 4rem;
}
.about-page .vision {
  width: 80%;
  margin: 0 auto;
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 2rem;
}
.about-page .vision .rig-logo {
  height: 4rem;
}
.about-page .mission {
  min-height: 30rem;
  clip-path: polygon(0 23%, 100% 0, 100% 100%, 0% 100%);
  background-color: #ec992f;
  display: flex;
  justify-content: center;
  align-items: center;
  gap: 2rem;
  padding: 4rem;
  margin-block: 4rem;
}
.about-page .mission .primary-heading {
  width: 30%;
  font-family: "Gabriela", serif;
  font-size: 6rem;
  font-weight: normal;
  color: transparent;
  -webkit-text-stroke: 2px rgba(255, 255, 255, 0.8);
}
.about-page .mission__img--wrapper {
  display: flex;
  gap: 2rem;
}
.about-page .mission__img {
  width: 40%;
  border-radius: 1rem;
}
.about-page .founder {
  display: flex;
  align-items: center;
  justify-content: space-around;
  margin: 6rem auto;
  gap: 2rem;
  padding: 4rem;
}
.about-page .founder .tertiary-heading {
  margin-bottom: 2rem;
}
.about-page .founder__info--wrapper {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 4rem;
  width: 25%;
}
.about-page .founder__info {
  background-color: #143143;
  padding: 1rem;
  border-radius: 0.5rem;
  width: 100%;
}
.about-page .founder__info--img {
  width: 100%;
}
.about-page .founder__social {
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin-top: 1rem;
}
.about-page .founder__social .icon {
  transition: all ease-in-out 0.2s;
}
.about-page .founder__social .icon:hover {
  scale: 1.15;
}
.about-page .founder__txt {
  width: 70%;
}
.about-page .founder__txt .paragraph {
  font-size: 1.4rem;
  margin-top: 2rem;
}
.about-page .about__footer {
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 80%;
  margin: 4rem auto;
}
.about-page .about__footer .paragraph {
  font-size: 1.8rem;
}
@media screen and (max-width: 800px) {
  .about-page .display-on-desktop {
    display: none !important;
  }
  .about-page .display-on-mobile {
    display: flex !important;
  }
  .about-page .secondary-heading {
    font-size: 1.8rem;
  }
  .about-page .mission {
    clip-path: polygon(0 10%, 100% 0, 100% 100%, 0% 100%);
    flex-direction: column;
  }
  .about-page .mission .primary-heading {
    width: 100%;
  }
  .about-page .mission__img {
    width: 50%;
  }
  .about-page .founder {
    flex-direction: column;
    margin: 4rem;
  }
  .about-page .founder__info--wrapper {
    width: 60%;
    margin-inline: auto;
  }
  .about-page .founder__txt {
    width: 100%;
  }
  .about-page .mobile-btn {
    width: fit-content;
    margin: 2rem auto;
  }
  .about-page .main .icon {
    filter: invert(90%);
    transition: all ease-in-out 0.2s;
  }
  .about-page .main .icon:active {
    scale: 1.15;
  }
}

.auth-page .container {
  min-height: 90vh;
  width: 100%;
  background-image: url("/57236c6cfa7fef2c17b961060f40bd24.png");
  background-position: center;
  position: relative;
}
.auth-page .header {
  width: 100%;
  height: fit-content;
  display: block;
  background: unset;
  position: relative;
}
.auth-page .main {
  width: 45%;
  margin: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding-block: 6rem;
}
.auth-page .main__img {
  width: 100%;
  display: inline-block;
  margin-block: 8rem 4rem;
}
.auth-page .main a {
  color: #ec992f;
}
.auth-page .form {
  display: flex;
  flex-direction: column;
  gap: 2rem;
  padding: 4rem 2rem 2rem;
  margin-bottom: 2rem;
  width: 100%;
  background-color: #fff;
  border: 1px solid #cccccc;
  box-shadow: 0px 82px 33px rgba(34, 41, 55, 0.01), 0px 46px 28px rgba(34, 41, 55, 0.03), 0px 20px 20px rgba(34, 41, 55, 0.04), 0px 5px 11px rgba(34, 41, 55, 0.05), 0px 0px 0px rgba(34, 41, 55, 0.05);
  border-radius: 1rem;
}
.auth-page .form__item {
  width: 100%;
  display: flex;
  padding-inline: 1rem;
  gap: 1rem;
  align-items: center;
  outline: 2px solid rgba(72, 84, 97, 0.2);
  background-color: rgba(255, 255, 255, 0.8);
  border-radius: 0.5rem;
}
.auth-page .form__input, .auth-page .form__input:focus {
  border: unset;
  outline: unset;
  background-color: transparent;
  padding: 1rem;
  width: 100%;
}
.auth-page .submit-btn {
  width: 60%;
  margin-top: 2rem;
  margin-inline: auto;
}
.auth-page .pswd-icon {
  cursor: pointer;
}
.auth-page .remember {
  display: flex;
  align-items: center;
  gap: 1rem;
}
.auth-page input[type=checkbox] {
  display: flex;
  align-items: center;
  justify-content: center;
  appearance: none;
  -webkit-appearance: none;
  background-color: #ec992f;
  height: 2rem;
  width: 2rem;
  outline: none;
  border-radius: 0.6rem;
  cursor: pointer;
}
.auth-page input[type=checkbox]::after {
  content: url("/51ae70bd674b8ecf060cdf3912e328c2.png");
  display: none;
  margin: 0;
  height: 2rem;
  width: 2rem;
}
.auth-page input[type=checkbox]:hover {
  background-color: #ffb266;
}
.auth-page input[type=checkbox]:checked {
  background-color: #fff;
}
.auth-page input[type=checkbox]:checked::after {
  display: inline-block;
}
@media screen and (max-width: 650px) {
  .auth-page .main {
    width: 90%;
  }
}

.signup hr {
  background-color: rgba(72, 84, 97, 0.2);
}
.signup .info {
  font-size: 1.4rem;
  padding-left: 1rem;
}
.signup .info .icon {
  width: 1rem;
}
.signup .partner-info {
  text-align: center;
}
.signup .partner-info .secondary-heading {
  font-weight: normal;
  text-decoration: underline;
  margin-bottom: 2rem;
}
.signup .partner-info .change-partnership {
  color: #1e293b;
  text-decoration: underline;
}
.signup .bottom-txt {
  margin-inline: auto;
}
.signup .bottom-txt a {
  text-decoration: underline;
}

.contact-page {
  background-color: rgba(72, 84, 97, 0.2);
}
.contact-page .navbar {
  width: 90%;
  position: static;
}
.contact-page .secondary-heading {
  font-size: 2.8rem;
  margin-bottom: 0;
}
.contact-page .main {
  margin-top: 6rem;
  margin-inline: auto;
  width: 80%;
  position: relative;
}
.contact-page .top-bar {
  margin: 0;
  padding: 1rem;
  background-color: transparent;
  border-bottom: 2px solid rgba(72, 84, 97, 0.2);
}
.contact-page .top-bar .social-icon {
  margin-right: 1rem;
  filter: invert(90%);
  transition: all ease-in-out 0.2s;
}
.contact-page .top-bar .social-icon:hover {
  scale: 1.1;
  rotate: 10deg;
}
.contact-page .top-bar .social-icon:active {
  scale: 1.1;
  rotate: -10deg;
}
.contact-page .contact-main {
  margin-block: 2rem;
  display: flex;
  justify-content: space-between;
  gap: 4rem;
}
.contact-page .contact-main .form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  width: 45%;
}
.contact-page .contact-main .form__item, .contact-page .contact-main .form__input--wrapper {
  width: 100%;
  display: flex;
  gap: 1rem;
  align-items: center;
}
.contact-page .contact-main .form__input, .contact-page .contact-main .form__input:focus {
  border: unset;
  outline: 2px solid rgba(72, 84, 97, 0.2);
  padding: 1rem;
  width: 100%;
  border-radius: 0.5rem;
  background-color: rgba(255, 255, 255, 0.8);
}
.contact-page .contact-main .btn {
  max-width: 40%;
  margin: 2rem 6rem;
}
.contact-page .contact-main textarea {
  height: 12rem;
}
.contact-page .contact-main option {
  width: inherit;
}
.contact-page .contact__img--group {
  width: fit-content;
  margin: 6rem auto;
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  z-index: 1;
}
.contact-page .pattern {
  width: 25rem;
