  align-items: center;
  padding: 2rem;
  background-color: #fff;
  box-shadow: 2px 2px 10px 6px rgba(142, 142, 142, 0.15);
  border-radius: 2rem;
}
.testimonies__card--name {
  margin-bottom: 0;
  font-weight: 500;
}
.testimonies .swiper-pagination-bullet {
  background-color: #ec992f;
}

.cta {
  width: 100%;
  height: 80%;
  padding: 4rem 2rem;
  margin-top: auto;
  border-radius: 1rem;
  background-color: #ec6a20;
  color: #fff;
  display: flex;
  justify-content: space-between;
  position: relative;
}
.cta::after {
  display: block;
  content: "Be in the Know";
  font-family: "Gabriela", serif;
  font-size: 5rem;
  color: transparent;
  -webkit-text-stroke: 2px rgba(255, 255, 255, 0.4);
  top: 8rem;
  right: 25%;
  position: absolute;
  z-index: 1;
}
.cta__wrapper {
  width: 80%;
  height: 30rem;
  position: relative;
  display: flex;
  align-items: flex-end;
  margin: 4rem auto;
}
.cta__txt {
  width: 60%;
  display: flex;
  flex-direction: column;
}
.cta__txt--info {
  margin-block: 4rem 1rem;
}
.cta__form {
  width: 90%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  background-color: #fff;
  border-radius: 1.5rem;
  padding: 0.5rem;
}
.cta__form--input {
  border: unset;
  outline: unset;
  width: 70%;
  padding: 1rem;
}
.cta__form .btn {
  border-radius: 1.5rem;
  background-color: rgba(255, 178, 102, 0.8);
}
.cta__form--btn,
.cta__form .icon {
  height: 100%;
}
.cta__img {
  width: 25%;
  right: 4rem;
  align-self: flex-end;
  z-index: 2;
}
.cta__img .main-img {
  width: 100%;
}

@media screen and (max-width: 1024px) {
  .prophecy-section.split-2 {
    height: 25rem;
  }
  .about .tertiary-heading {
    margin-bottom: 0;
  }
}
@media screen and (max-width: 800px) {
  .welcome__img {
    width: 60%;
    order: -1;
    margin-top: 0;
  }
  .events .display-on-desktop {
    display: none !important;
  }
  .events .display-on-mobile {
    display: flex !important;
  }
  .events__txt {
    flex-direction: column;
    gap: 2rem;
    margin-bottom: 2rem;
  }
  .about .tertiary-heading {
    text-align: center;
    margin-bottom: 2rem;
    width: 100%;
  }
  .about__txt, .about__img {
    width: 80%;
    margin-inline: auto;
  }
  .about__main {
    flex-direction: column-reverse;
  }
  .partners {
    width: 80%;
  }
  .partners__wrapper {
    height: auto;
    padding-block: 4rem;
    clip-path: none;
  }
  .partners__wrapper::after {
    clip-path: none;
  }
  .prophecy-section.split-2 {
    flex-direction: column;
    height: unset;
  }
  .prophecy-section.split-2 .main-img {
    margin-top: -12rem;
    position: static;
    width: 80%;
  }
  .prophecy-section.split-2 .prophecy-section__txt {
    padding: 2rem;
  }
  .cta__wrapper {
    height: auto;
    align-items: flex-end;
  }
  .cta {
    padding: 2rem;
    justify-content: space-between;
  }
  .cta__txt {
    width: 70%;
  }
  .cta__txt--info {
    margin-block: 1rem;
    line-height: 1;
    font-size: 1.3rem;
  }
  .cta__form {
    width: 100%;
    border-radius: 1.5rem;
    padding: 0.5rem;
  }
  .cta__form--input {
    width: 70%;
    padding: 1rem;
  }
  .cta__form .btn {
    border-radius: 1.5rem;
  }
  .cta__img {
    display: none;
  }
  .cta::after {
    right: 0;
    top: 4rem;
