@media screen and (max-width: 450px) {
  .hero {
    text-align: center;
  }
  .welcome .welcome__txt--title,
  .welcome .welcome__txt--heading {
    margin-inline: 4rem;
  }
  .welcome .welcome__main {
    margin-top: 4rem;
  }
  .about .tertiary-heading {
    margin-inline: 4rem;
    margin-bottom: 4rem;
    width: inherit;
  }
  .about__main {
    padding: 0;
  }
  .cta::after {
    display: none;
  }
  .cta__txt {
    width: 100%;
  }
}
.backdrop {
  width: 100%;
  height: 100vh;
  height: 100svh;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #e6e4e4;
}

.modal {
  width: 40%;
  min-width: 34rem;
  padding: 6rem 2rem;
  border-radius: 2rem;
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  box-shadow: 2px 2px 10px 6px rgba(142, 142, 142, 0.15);
  position: relative;
}
.modal__close {
  position: absolute;
  right: 2rem;
  top: 2rem;
  cursor: pointer;
}
.modal__close .icon {
  width: 2rem;
}
.modal__img {
