@use '../abstracts/variables' as *;

.navbar {
	// width: 90%;
	height: $medium;
	display: flex;
	align-items: center;
	justify-content: center;
	padding: $tiny 1rem;
	margin: $tiny auto;
	border-radius: 1rem;
	background: rgba(217, 217, 217, 0.2);
	border: 1px solid rgba(30, 30, 30, 0.5);
	box-shadow: 0px 0.4rem 2.5rem rgba(0, 0, 0, 0.25);
	z-index: 50;
	position: fixed;
	left: $tiny;
	right: $tiny;
	top: $small;

	&__logo {
		display: inline-block;
		margin-right: auto;
		justify-self: start;
		width: 12%;
	}

	.logo {
		width: 100%;
	}

	.btn-primary {
		transform-origin: right;
	}
}

.navigation {
	display: flex;
	gap: 1.5rem;
	text-transform: capitalize;
	font-size: 1.5rem;
	margin-inline: $tiny;
	// flex-wrap: wrap;

	&__link {
		cursor: pointer;
		transition: all cubic-bezier(0.23, 1, 0.32, 1) 0.2s;
	}

	&__link:hover {
		color: $primaryColor;
	}
}

.search-icon {
	display: inline-block;
	margin-left: $tiny;
	cursor: pointer;
}

.nav-toggle {
	display: none;
	filter: invert(80%);
	margin-left: $tiny;
}

// ==================== MEDIA QUERIES ================
@media screen and (max-width: 800px) {
	.nav-toggle {
		display: inline-block;
	}

	nav {
		.navbar {
			position: relative;
			left: 0;
			right: 0;
		}

		.navigation {
			background-color: $textColor;
			color: $backgroundColor;
			flex-direction: column;
			position: absolute;
			top: $medium;
			right: 0;
			padding: $tiny $small;
			border-radius: 1rem;
			z-index: 60;

			&__link {
				width: fit-content;
			}
		}

		.navbar__logo {
			width: 30%;
		}
	}
}

sources.assets\\sass\\components\\_buttons.scss:
@use '../abstracts/variables' as *;

.btn {
	min-width: max-content;
	padding: 0.5rem 1rem;
	display: inline-block;
	border-radius: 0.5rem;
	border: unset;
	outline: unset;
	cursor: pointer;
	display: flex;
	align-items: center;
	justify-content: center;

	a {
		color: inherit;
	}
}

.btn-primary {
	font-size: 2rem;
	padding: 1rem 2rem;
	text-transform: capitalize;
	background-color: $primaryColor;
	color: $backgroundColor;
	transition: all cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s;
	transform-origin: left;
}

.btn-primary:hover {
	background-color: $brightOrange;
	scale: 1.05;
}

.btn-primary:active {
	translate: 1rem;
	scale: 1.05;
}

.btn-secondary {
	font-size: 2rem;
	padding: 0.5rem 1rem;
	text-transform: capitalize;
	background-color: transparent;
	border: 2px solid $primaryColor;
	color: $backgroundColor;
	transition: all cubic-bezier(0.175, 0.885, 0.32, 1.275) 0.2s;
}

.btn-secondary:hover {
	border: 2px solid $brightOrange;
}

@media screen and (max-width: 650px) {
	.btn-primary {
		font-size: 1.6rem;
	}
}

