sources.assets\\sass\\layouts\\_header.scss:
@use '../abstracts/variables' as *;

// NEWSLINE (TOP OF PAGE)
.newsline {
	display: flex;
	align-items: center;
	justify-content: center;
	flex-wrap: wrap;
	gap: $medium;
	padding: $tiny $small;
	background-color: $backgroundColor;
	height: 5rem;
	margin-bottom: $tiny;
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	z-index: 50;

	&__txt {
		display: flex;
		align-items: baseline;
		gap: 0.2rem;

		&--heading {
			text-transform: uppercase;
			font-weight: 400;
		}

		&--link {
			text-transform: capitalize;
			color: $primaryColor !important;
		}
	}

	&__link--group {
		display: flex;
		gap: $tiny;
	}

	&__link {
		height: $tiny;
		transition: cubic-bezier(0.6, -0.28, 0.735, 0.045) 0.2s;
	}

	&__link:hover {
		scale: 1.15;
		// rotate: 10deg;
	}
}

.header {
	// position: static;
	background-image: url('/assets/images/hero-bg.png');
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center;
	background-blend-mode: screen;
	background-color: rgb(8, 23, 39);
	height: 100vh;
	height: 100dvh;
	width: 100%;
	top: $small;

	&__wrapper {
		display: flex;
		flex-direction: column;
		align-items: flex-end;
		color: $backgroundColor;
		height: 100%;
		background-image: linear-gradient(rgb(40, 49, 59), rgba(72, 84, 97, 0.46));
		padding: $small;
	}

	.search-form {
		display: flex;
		align-items: center;
		position: fixed;
		top: 15rem;
		margin-right: $big;
		z-index: 20;

		&__label {
			display: flex;
			align-items: center;
			height: 100%;
			background-color: rgba($accentColor, 0.5);
			outline: 2px solid $accentColor;
			border-radius: 0.5rem;
			margin-left: 1rem;
		}

		&__input,
		&__input:focus {
			background-color: transparent;
			border: unset;
			color: $backgroundColor;
			outline: unset;
			height: 100%;
			padding: 1rem;
		}

		&__input::placeholder {
			color: $primaryColor;
			text-transform: capitalize;
			font-weight: bold;
		}

		&__close {
			display: inline-block;
			color: $backgroundColor;
			padding-inline: 1rem;
			cursor: pointer;
		}

		.toggle-form {
			cursor: pointer;
			filter: invert(80%);
		}
	}
}

// ============= HERO SECTION ==============
.hero {
	display: block;
	margin-block: auto 0;
	width: 100%;

	&__txt {
		width: 50%;
		text-align: center;
		margin-inline: auto;
		display: flex;
		flex-direction: column;
		align-items: center;
		gap: 1rem;

		&--heading {
			font-size: clamp(2rem, 4vw + 0.5rem, 4rem);
		}
	}

	&__info {
		display: flex;
		justify-content: space-between;
		margin-top: $big;

		&--stats {
			display: flex;
			gap: $small;
		}
	}

	.stat {
		display: flex;
		flex-direction: column;
		align-items: center;
	}

	&__logo {
		position: relative;
		display: flex;
		align-items: center;
		justify-content: center;

		.rig-logo {
			height: $small;
		}

		.rig-txt {
			height: $big;
			position: absolute;
			animation: rotate 15s linear infinite;
			z-index: 5;
		}
	}
}

// ================ MEDIA QUERIES ======================
@media screen and (max-width: 800px) {
	.hide-nav {
		display: none !important;
		height: 0;
	}

	.header__wrapper {
		padding: 0;
	}

	.newsline {
		height: fit-content;
		padding: 1rem $tiny;
		justify-content: center;
		// position: static;
		width: 100%;

		&__link--group {
			display: none;
		}
	}

	.hero__txt {
		width: 80%;
		margin-block: auto $medium;
	}

	.hero__logo {
		display: none;
	}

	.hero__info {
		margin: $small 1rem;
		gap: $small;
		flex-direction: column;
		align-items: flex-end;

		&--stats {
			align-self: center;
		}

		.stat__info {
			font-size: 1.4rem;
		}
	}
}

sources.assets\\sass\\layouts\\footer.scss:
@use '../abstracts/variables' as *;

@use '../abstracts/mixins' as *;

// =============== FOOTER SECTION =============
.footer {
	display: flex;
	align-items: center;
	justify-content: space-evenly;
	padding: $medium;
	background-color: $secondaryColor;
	color: $backgroundColor;
	position: relative;

	@include desktop-display(flex);

	&__logo {
		position: relative;
		display: flex;
		align-items: center;
		justify-content: center;

		.rig-logo {
			height: $small;
		}

		.rig-txt {
			height: $big;
			position: absolute;
			animation: rotate 15s linear infinite;
			z-index: 5;
		}
	}

	// FOOTER MAIN CONTENT
	&__main {
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		margin-inline: $small;
		gap: 1rem;
	}

	&__links {
		display: flex;
		align-items: center;
	}

	.navigation,
	.paragraph {
		font-size: 1.2rem;
	}

	.nav-group {
		display: flex;
		gap: 1rem;
	}

	&__btn-group {
		display: flex;
		align-items: center;
		gap: $small;

		.btn {
			padding: 0.5rem 1.5rem;
			font-size: 1.8rem;
		}
	}

	// SOCIAL ICONS
	&__socials {
		display: flex;
		gap: $tiny;

		.icon {
			transition: all ease-in-out 0.2s;
		}

		.icon:hover {
			scale: 1.15;
		}
	}
}

// ==================== MEDIA QUERIES ================
@media screen and (max-width: 800px) {
	.footer {
		padding: $small;
		// justify-content: flex-start;

		@include mobile-display(flex);

		&__main {
			margin: $tiny;
		}

		&__logo {
			margin-right: auto;

			.rig-logo {
				height: $tiny;
			}

			.rig-txt {
				height: $small;
			}
		}

		&__links {
			flex-direction: column-reverse;
		}

		.navigation {
			align-items: flex-start;
			margin-block: $tiny;
			gap: $small;
		}

		.nav-group {
			flex-direction: column;
			align-items: flex-start;
		}
	}
}

sources.assets\\sass\\abstracts\\_mixins.scss:
@mixin mobile-display($mobile-display: flex) {
	.display-on-desktop {
		display: none !important;
	}

	.display-on-mobile {
		display: $mobile-display !important;
	}
}

@mixin desktop-display($desktop-display: flex) {
	.display-on-mobile {
		display: none !important;
	}

	.display-on-desktop {
		display: $desktop-display !important;
	}
}

sources.assets\\sass\\layouts\\_about.scss:
// ============ ABOUT PAGE STYLE =====================
@use '../abstracts/variables' as *;

@use 'abstracts/mixins' as *;

.about-page {
	background-color: rgba($accentColor, 0.2);

	@include desktop-display;

	.header {
		background-image: url('/assets/images/about-bg.png');
		max-height: 56rem;

		&__wrapper {
			background: rgba(236, 153, 47, 0.2);
		}
	}

	.hero {
		width: 80%;
		margin: auto;
		display: flex;
		align-items: center;
		justify-content: center;
	}

	.about__logo {
		width: 100%;
	}

	.main {
		margin-block: $small;
	}

	// =============== VISION SECTION ===============
	.vision {
		width: 80%;
		margin: 0 auto;
		text-align: center;
		display: flex;
		flex-direction: column;
		justify-content: center;
		align-items: center;
		gap: $tiny;

		.rig-logo {
			height: $small;
		}
	}

	// =============== MISSION SECTION ===============
	.mission {
		min-height: 30rem;
		clip-path: polygon(0 23%, 100% 0, 100% 100%, 0% 100%);
		background-color: $primaryColor;
		display: flex;
		justify-content: center;
		align-items: center;
		gap: $tiny;
		padding: $small;
		margin-block: $small;

		.primary-heading {
			width: 30%;
			font-family: 'Gabriela', serif;
			font-size: 6rem;
			font-weight: normal;
			color: transparent;
			-webkit-text-stroke: 2px rgba($backgroundColor, 0.8);
		}

		&__img--wrapper {
			display: flex;
			gap: $tiny;
		}

		&__img {
			width: 40%;
			border-radius: 1rem;
		}
	}

	// FOUNDER SECTION
	.founder {
		display: flex;
		align-items: center;
		justify-content: space-around;
		margin: $medium auto;
		gap: $tiny;
		padding: $small;

		.tertiary-heading {
			margin-bottom: $tiny;
		}

		&__info--wrapper {
			display: flex;
			flex-direction: column;
			align-items: center;
			gap: $small;
			width: 25%;
		}

		&__info {
			background-color: #143143;
			padding: 1rem;
			border-radius: 0.5rem;
			width: 100%;

			&--img {
				width: 100%;
			}
		}

		&__social {
			display: flex;
			justify-content: center;
			gap: 1rem;
			margin-top: 1rem;

			.icon {
				transition: all ease-in-out 0.2s;
			}

			.icon:hover {
				scale: 1.15;
			}
		}

		&__txt {
			width: 70%;

			.paragraph {
				font-size: 1.4rem;
				margin-top: $tiny;
			}
		}
	}

	.about__footer {
		display: flex;
		flex-direction: column;
		align-items: center;
		width: 80%;
		margin: $small auto;

		.paragraph {
			font-size: 1.8rem;
		}
	}

	// ============== MEDIA QUERIES ==================
	@media screen and (max-width: 800px) {
		@include mobile-display;

		.secondary-heading {
			font-size: 1.8rem;
		}

		.mission {
			clip-path: polygon(0 10%, 100% 0, 100% 100%, 0% 100%);
			flex-direction: column;

			.primary-heading {
				width: 100%;
			}

			&__img {
				width: 50%;
			}
		}

		.founder {
			flex-direction: column;
			margin: $small;

			&__info--wrapper {
				width: 60%;
				margin-inline: auto;
			}

			&__txt {
				width: 100%;
			}
		}

		.mobile-btn {
			width: fit-content;
			margin: $tiny auto;
		}

		.main {
			.icon {
				filter: invert(90%);
				transition: all ease-in-out 0.2s;
			}

			.icon:active {
				scale: 1.15;
			}
		}
	}
}

sources.assets\\sass\\layouts\\_auth.scss:
// =============== sTYLESHEEET FOR LOGIN AND REGISTER PAGE
@use '../abstracts/variables' as *;

.auth-page {
	.container {
		min-height: 90vh;
		width: 100%;
		background-image: url('/assets/images/shokunin_World_Map\ 3.png');
		// background-size: contain;
		// background-repeat: no-repeat;
		background-position: center;
		position: relative;
	}

	.header {
		width: 100%;
		height: fit-content;
		display: block;
		background: unset;
		position: relative;
	}

	.main {
		width: 45%;
		margin: auto;
		display: flex;
		flex-direction: column;
		align-items: center;
		justify-content: center;
		padding-block: $medium;

		&__img {
			width: 100%;
			display: inline-block;
			margin-block: $big $small;
		}

		a {
			color: $primaryColor;
		}
	}

	.form {
		display: flex;
		flex-direction: column;
		gap: $tiny;
		padding: $small $tiny $tiny;
		margin-bottom: $tiny;
		width: 100%;
		background-color: $backgroundColor;
		border: 1px solid #cccccc;
		box-shadow: 0px 82px 33px rgba(34, 41, 55, 0.01),
			0px 46px 28px rgba(34, 41, 55, 0.03), 0px 20px 20px rgba(34, 41, 55, 0.04),
			0px 5px 11px rgba(34, 41, 55, 0.05), 0px 0px 0px rgba(34, 41, 55, 0.05);
		border-radius: 1rem;

		&__item {
			width: 100%;
			display: flex;
			padding-inline: 1rem;
			gap: 1rem;
			align-items: center;
			outline: 2px solid rgba($accentColor, 0.2);
			background-color: rgba($backgroundColor, 0.8);
			border-radius: 0.5rem;
		}

		&__input,
		&__input:focus {
			border: unset;
			outline: unset;
			background-color: transparent;
			padding: 1rem;
			width: 100%;
		}
	}

	.submit-btn {
		width: 60%;
		margin-top: $tiny;
		margin-inline: auto;
	}

	.pswd-icon {
		cursor: pointer;
	}

	.remember {
		display: flex;
		align-items: center;
		gap: 1rem;
	}

	input[type='checkbox'] {
		display: flex;
		align-items: center;
		justify-content: center;
		appearance: none;
		-webkit-appearance: none;
		background-color: $primaryColor;
		height: $tiny;
		width: $tiny;
		outline: none;
		border-radius: 0.6rem;
		cursor: pointer;

		&::after {
			content: url('/assets/icons/tick-square.png');
			display: none;
			margin: 0;
			height: $tiny;
			width: $tiny;
		}
	}

	input[type='checkbox']:hover {
		background-color: $paleOrange;
	}

	input[type='checkbox']:checked {
		background-color: $backgroundColor;
	}

	input[type='checkbox']:checked::after {
		display: inline-block;
	}

	// =============== MEDIA QUERIES =======================
	@media screen and (max-width: 650px) {
		.main {
			width: 90%;
		}
	}
}

.signup {
	hr {
		background-color: rgba($accentColor, 0.2);
	}

	.info {
		font-size: 1.4rem;
		padding-left: 1rem;

		.icon {
			width: 1rem;
		}
	}

	.partner-info {
		text-align: center;

		.secondary-heading {
			font-weight: normal;
			text-decoration: underline;
			margin-bottom: $tiny;
		}

		.change-partnership {
			color: $textColor;
			text-decoration: underline;
		}
	}

	.bottom-txt {
		margin-inline: auto;

		a {
			text-decoration: underline;
		}
	}
}

sources.assets\\sass\\layouts\\_contact.scss:
// =============== CONTACT US PAGE STYLES ====================
@use '../abstracts/variables' as *;

.contact-page {
	background-color: rgba($accentColor, 0.2);

	.navbar {
		width: 90%;
		position: static;
	}

	.secondary-heading {
		font-size: 2.8rem;
		margin-bottom: 0;
	}

	.main {
		margin-top: $medium;
		margin-inline: auto;
		width: 80%;
		position: relative;
	}

	.top-bar {
		margin: 0;
		padding: 1rem;
		background-color: transparent;
		border-bottom: 2px solid rgba($accentColor, 0.2);

		.social-icon {
			margin-right: 1rem;
			filter: invert(90%);
			transition: all ease-in-out 0.2s;
		}

		.social-icon:hover {
			scale: 1.1;
			rotate: 10deg;
		}

		.social-icon:active {
			scale: 1.1;
			rotate: -10deg;
		}
	}

	.contact-main {
		margin-block: $tiny;
		display: flex;
		justify-content: space-between;
		gap: $small;

		.form {
			display: flex;
			flex-direction: column;
			gap: 1rem;
			width: 45%;

			&__item,
			&__input--wrapper {
				width: 100%;
				display: flex;
				gap: 1rem;
				align-items: center;
			}

			&__input,
			&__input:focus {
				border: unset;
				outline: 2px solid rgba($accentColor, 0.2);
				padding: 1rem;
				width: 100%;
				border-radius: 0.5rem;
				background-color: rgba($backgroundColor, 0.8);
			}
		}

		.btn {
			max-width: 40%;
			margin: $tiny $medium;
		}

		textarea {
			height: 12rem;
		}

		option {
			width: inherit;
		}
	}

	.contact__img--group {
		width: fit-content;
		margin: $medium auto;
		display: flex;
		align-items: center;
		justify-content: center;
		position: relative;
		z-index: 1;
	}

	.pattern {
		width: 25rem;
	}

	.center-img {
		width: 20rem;
		z-index: 2;
		left: -1rem;
		bottom: 1rem;
	}

	.top-img {
		width: $large;
		z-index: 3;
		top: 0;
		left: -1rem;
	}

	.bottom-img {
		width: $large;
		z-index: 3;
		bottom: -1rem;
		left: 8rem;
	}

	.center-img,
	.top-img,
	.bottom-img {
		border-radius: 50%;
		position: absolute;
	}

	.rig-branch__group {
		width: 100%;
		display: grid;
		grid-template-columns: repeat(auto-fit, minmax(6rem, 1fr));
		gap: $small;
		align-items: center;
		margin-block: $tiny $medium;
	}

	.rig-branch {
		width: 100%;
		display: inline-block;
		transition: all ease-in-out 0.2s;
	}

	.rig-branch:hover {
		transform: translateY(-1rem);
	}

	.rig-branch:active {
		transform: translateY(1rem);
	}

	.rig-img {
		width: inherit;
		object-fit: cover;
		cursor: pointer;
	}
}

// =================MEDIA QUERIES =======================
@media screen and (max-width: 800px) {
	.contact-page {
		.top-bar {
			flex-wrap: wrap;
		}

		.tertiary-heading {
			margin: 1rem 0;
		}

		.contact-main {
			flex-direction: column-reverse;
			align-items: center;

			.form {
				width: 80%;
			}
		}
	}
}

@media screen and (max-width: 450px) {
	.contact-page {
		.contact-main {
			.form {
				width: 100%;
			}
		}

		.secondary-heading {
			margin-inline: 0;
		}
	}
}

sources.assets\\sass\\layouts\\_dashboard.scss:
// STYLES FOR THE PARTNERS DASHBOARD PAGE
@use '../abstracts/variables' as *;
@use 'abstracts/mixins' as *;

.dashboard-page {
	background-color: #f7f8fa;
	@include desktop-display;

	.profile-card {
		display: flex;
		flex-direction: column;
		padding-block: 1rem;

		.user-info {
			display: flex;
			gap: 0.5rem;
		}

		.user-info__avatar {
			max-width: 6rem;
			border-radius: 50%;
		}

		&--email {
			font-size: 1.2rem;
		}
	}

	.main {
		padding: 0;
		margin: 14rem $tiny 0;
		position: relative;
	}

	// ==================== DASHBOARD CONTAINER =================
	.dashboard-wrapper {
		grid-template-columns: 20rem auto;
		gap: $small;
		// margin-top: $medium;
		padding: $small;
		position: relative;
	}

	// ======================= SIDEBAR ====================
	.sidebar {
		background-color: $backgroundColor;
		display: flex;
		flex-direction: column;
		margin-top: $small;
		padding-bottom: $medium;
		position: sticky;
		width: inherit;
		height: 65vh;
		height: 65dvh;

		// SIDEBAR NAV
		&__nav {
			display: flex;
			flex-direction: column;
			width: 100%;
			padding-left: $tiny;
			margin-bottom: auto;

			&--item {
				display: flex;
				gap: $tiny;
				align-items: center;
				justify-content: flex-start;
				margin-top: 0.5rem;
				padding: 1rem;
				border-top-left-radius: 1rem;
				border-bottom-left-radius: 1rem;
			}
		}

		.active-tab {
			background-color: #f7f8fa;

			.icon {
				filter: invert(73%) sepia(27%) saturate(2131%) hue-rotate(338deg)
					brightness(97%) contrast(91%);
			}
		}

		// SIDEBAR PROFILE CARD
		.profile-card {
			background-color: rgba($accentColor, 0.1);
			margin: $tiny;
			padding: 0.5rem;
			border-radius: 0.5rem;

			.user-info {
				&__avatar {
					width: 2rem;
					aspect-ratio: 1/1;
				}

				&__name {
					font-size: 1.6rem;
				}
			}

			&--email {
				margin-bottom: $tiny;
			}
		}
	}

	// ================ MAIN CONTAINER ==============
	.main-content {
		.topbar {
			display: flex;
			align-items: center;
			justify-content: space-between;

			.profile-card {
				flex-direction: row;
				align-items: center;
				gap: 1rem;

				.user-info {
					flex-direction: column;

					&__name {
						margin: 0;
					}

					&__avatar {
						width: $small;
						margin: 0;
					}
				}
			}
		}

		.main-card {
			width: 100%;
			padding-right: $small;
		}

		.dashboard {
			grid-template-columns: 30% 36% 32%;
			gap: $tiny;
			padding: $small 0;
			border-bottom: 2px solid rgba($accentColor, 0.1);
			margin-bottom: $small;

			&__card {
				background-color: $backgroundColor;
				border: 2px solid rgba($accentColor, 0.1);
				display: flex;
				flex-direction: column;
				align-items: center;
				justify-content: center;
				padding: 1rem 0;

				.tertiary-heading {
					font-size: 1.6rem;
				}
			}

			&__card:nth-of-type(2) {
				border: 3px solid $primaryColor;
				scale: 1.1;
			}
		}

		// PAYMENT HISTORY SECTION
		.history__heading--wrapper {
			display: flex;
			justify-content: space-between;
			padding: 1rem $small;
			margin-right: $small;
		}

		.history {
			&__wrapper {
				background-color: $backgroundColor;
				padding: $tiny;
				border-radius: 1rem;
			}

			&__item {
				display: flex;
				justify-content: space-between;
				border-bottom: 2px solid rgba($accentColor, 0.1);
				width: 100%;
				padding: 1rem $small;
			}
		}

		// ============ UPDATE PROFILE FORM ==============
		.form {
			margin-block: $small;
			padding: 0;

			&__group {
				grid-template-columns: repeat(2, 1fr);
				gap: $tiny;
				margin-block: $tiny;
				width: 100%;
			}

			&__item {
				width: inherit;
				display: flex;
				flex-direction: column;

				.label {
					text-transform: capitalize;
					font-size: 1.4rem;
					color: rgba($accentColor, 0.8);
					display: inline-block;
					margin: 0.5rem 1rem;
				}

				&--input {
					width: 100%;
					height: $small;
					border: unset;
					outline: 2px solid rgba($accentColor, 0.1);
					border-radius: 0.5rem;
					padding: 1rem;
					background-color: rgba($backgroundColor, 0.4);
				}
			}

			hr {
				border: 1px solid rgba($accentColor, 0.1);
				margin-bottom: $small;
			}

			.btn {
				margin: $small auto;
			}
		}
	}

	// PREVIOUS PAYMENT PAGINATION
	.pagination {
		display: flex;
		gap: 2px;
		margin: 0 auto;

		&__wrapper {
			display: flex;
			justify-content: space-between;
			margin: $tiny 0;
			width: 100%;
		}

		.btn {
			font-size: 1.2rem;
			border-radius: 0;

			.icon {
				height: 1rem;
				filter: invert(80%);
			}

			.previous {
				transform: rotateY(180deg);
			}
		}

		&-btn-group {
			gap: 1rem;

			.active-btn {
				background-color: $primaryColor;
			}
		}
	}

	.profile-section {
		width: 90%;
	}

	// ================= MEDIA QUERIES =============

	@media screen and (max-width: 850px) {
		.navbar {
			background-color: rgba($backgroundColor, 0.4);
		}

		.profile-section {
			width: 100%;
		}

		.main-content {
			.main-card {
				padding: 0;
			}

			.dashboard {
				gap: 1rem;

				.secondary-heading,
				.tertiary-heading {
					font-size: 1.2rem;
					text-align: center;
				}
			}
		}

		.dashboard-wrapper {
			padding-inline: $tiny;
		}

		.topbar {
			gap: 1rem;

			.btn-primary {
				font-size: 1.4rem;
				padding: 1rem;
			}
		}
	}

	@media screen and (max-width: 650px) {
		@include mobile-display;

		.hide-sidebar {
			display: none !important;
		}

		.mobile-nav-toggle {
			width: 100%;
			display: flex;
			gap: 1rem;
			align-items: center;

			.secondary-heading {
				width: 100%;
				background-color: rgba($backgroundColor, 1);
				padding: 1rem;
			}
		}

		.sidebar {
			position: absolute;
			left: 0;
			height: fit-content;
			width: 20rem;
			background-color: transparent;
			margin: 0;
			top: 0;
			padding: 0;
			z-index: 5;
			transition: all ease-in-out 2s;

			&__nav--item {
				background-color: rgba($backgroundColor, 1);
				border-radius: 2px;
				box-shadow: 0px 0.4rem 2.5rem rgba(0, 0, 0, 0.25);
			}

			.active-tab {
				background-color: rgba($backgroundColor, 1);
			}
		}

		.dashboard-wrapper {
			grid-template-columns: 1fr;
			padding: $tiny;
		}

		.topbar {
			flex-direction: row-reverse;
		}

		.profile-section {
			width: 80%;
			margin-inline: auto;

			.topbar {
				flex-direction: column;
				align-items: flex-start;
			}
		}

		.main-content {
			.form__group {
				grid-template-columns: 1fr;
			}

			hr {
				margin-top: $small;
			}

			.history__heading--wrapper,
			.history__item {
				padding-inline: 1rem;
			}
		}
	}

	@media screen and (max-width: 450px) {
		.profile-section {
			width: 100%;
		}
	}
}

