// ================== EVENTS PAGE STYLES =======
@use '../abstracts/variables' as *;
@use 'abstracts/mixins' as *;

.navbar {
	width: 90%;
}

.top-bar {
	display: flex;
	justify-content: space-between;
	align-items: center;
	padding: 1rem 2rem;
	border-radius: 0.5rem;
	width: 100%;
	background-color: rgba($backgroundColor, 0.8);
	margin-bottom: $tiny;

	.tertiary-heading {
		display: flex;
		align-items: center;
	}

	.search-form {
		display: flex;
		align-items: center;

		&__label {
			display: flex;
			align-items: center;
			height: 100%;
			background-color: rgba($accentColor, 0.2);
			outline: 2px solid $accentColor;
			border-radius: 0.5rem;
			margin-left: 1rem;
		}

		&__input,
		&__input:focus {
			background-color: transparent;
			border: unset;
			outline: unset;
			height: 100%;
			padding: 1rem;
		}

		&__close {
			display: inline-block;
			color: $backgroundColor;
			padding-inline: 1rem;
			cursor: pointer;
		}

		.toggle-form {
			cursor: pointer;
			filter: invert(80%);
		}
	}
}

.empty-event {
	width: 100%;
	display: flex;
	align-items: center;
	justify-content: center;
	gap: 1rem;
	text-transform: capitalize;
}

.date,
.online {
	display: flex;
	align-items: flex-end;
}

.events-page {
	background-color: rgba($accentColor, 0.2);

	.navbar {
		background-color: rgba($accentColor, 0.6);
		color: $backgroundColor;
		top: 0;
	}

	.main {
		margin-inline: auto;
		width: 70%;
		padding-top: 12rem;
	}

	.events__nav {
		display: flex;
		align-items: center;
		justify-content: space-between;
		margin-block: $small;
	}

	.date__nav {
		display: flex;
		align-items: center;

		.btn {
			margin-right: 1rem;

			.icon {
				transform: rotateY(180deg);
				filter: invert(80%);
			}
		}
	}

	.events {
		width: 100%;

		.events-section {
			margin-bottom: $small;
		}

		.event {
			display: flex;
			flex-direction: row;
			background-color: rgba($backgroundColor, 0.8);
			align-items: center;
			margin-bottom: $tiny;
			padding: 1rem;
			border-radius: 0.5rem;

			&__img {
				width: 15rem;
				object-fit: contain;
			}

			&__txt {
				display: flex;
				flex-direction: column;
				gap: 1rem;
				padding: 1rem;
			}

			&__date {
				display: flex;
				align-items: flex-end;
				width: 100%;
				gap: 0.5rem;
			}
		}
	}

	.cta__wrapper {
		width: 100%;
	}

	// hide moving text in the CTA
	.cta::after {
		display: none;
	}
}

// ==================== PAST EVENTS STYLING =================
.past-events-page {
	font-size: 1.4rem;
	background-color: rgba($accentColor, 0.2);

	@include desktop-display;

	.navbar {
		background-color: rgba($accentColor, 0.6);
		color: $backgroundColor;
		top: 0;
	}

	.main {
		margin-inline: auto;
		width: 70%;
		padding-top: 12rem;
	}

	.events__nav {
		display: flex;
		align-items: center;
		justify-content: space-between;
		margin-bottom: $tiny;
	}

	.date__nav {
		display: flex;
		align-items: center;

		.btn {
			margin-right: 1rem;

			.icon {
				transform: rotateY(180deg);
				filter: invert(80%);
			}
		}
	}

	.events {
		width: 100%;
		margin-block: $tiny;

		.events-section {
			margin-bottom: $tiny;
			background-color: rgba($backgroundColor, 0.8);
		}

		.event {
			display: flex;
			align-items: center;
			flex-direction: row;
			margin-bottom: $tiny;
			padding: 1rem;
			border-bottom: 2px solid rgba($accentColor, 0.2);

			&__img {
				height: 15rem;
				width: 15rem;
				object-fit: cover;
			}

			.secondary-heading {
				margin: 0;
			}

			&__txt {
				display: flex;
				flex-direction: column;
				align-items: flex-start;
				background-color: rgba($backgroundColor, 0.8);
				gap: 1rem;
				padding: 1rem;
			}

			&__date {
				display: flex;
				align-items: flex-end;
				width: 100%;
				gap: 0.5rem;
			}
		}
	}

	.pagination {
		display: flex;
		gap: 2px;
		margin: 0 auto;

		&__wrapper {
			display: flex;
			justify-content: space-between;
			margin: $tiny 0;
			width: 100%;
		}

		.btn {
			font-size: 1.2rem;
			border-radius: 0;

			.icon {
				height: 1rem;
				filter: invert(80%);
			}

			.previous {
				transform: rotateY(180deg);
			}
		}

		&-btn-group {
			gap: 1rem;

			.active-btn {
				background-color: $primaryColor;
			}
		}
	}

	.cta__wrapper {
		width: 100%;
	}

	// hide moving text in the CTA
	.cta::after {
		display: none;
	}
}

// ================ MEDIA QUERIES ====================
@media screen and (max-width: 800px) {
	//  top-bar padding for event pages
	.past-events-page,
	.events-page {
		.top-bar {
			padding: 0 1rem;
		}
	}

	.past-events-page {
		.main {
			width: 85%;
		}

		.events {
			// width: 80%;

			.event {
				align-items: stretch;

				&__img {
					height: auto;
				}
			}
		}
	}
}

@media screen and (max-width: 650px) {
	.events-page {
		.events {
			// width: 80%;

			.event {
				flex-direction: column;

				&__img {
					width: 100%;
					// height: auto;
				}

				&__txt {
					width: 100%;
				}

				&__date {
					flex-wrap: wrap;
				}
			}
		}
	}

	.past-events-page {
		@include mobile-display;
	}
}
@media screen and (max-width: 550px) {
	.events-page,
	.past-events-page {
		.secondary-heading,
		.tertiary-heading {
			margin-inline: 0;
		}

		.main {
			width: 80%;
		}

		.top-bar {
			background-color: transparent;
			padding-inline: 0;

			.toggle-form {
				display: none;
			}
		}

		.search-form {
			position: absolute;
			right: $small;
			top: $large;
			z-index: 5;
		}

		.events {
			// width: 80%;

			.event {
				flex-direction: column;

				&__img {
					width: 100%;
					// height: auto;
				}

				&__txt {
					width: 100%;
				}

				&__date {
					flex-wrap: wrap;
				}
			}
		}
	}
}

sources.assets\\sass\\layouts\\_home.scss:
// =============== LANDING PAGE STYLES =========
@use '../abstracts/variables' as *;
@use 'abstracts/mixins' as *;

// ================ WELCOME SECTION ==============
.welcome {
	margin-top: $medium;
	margin-inline: auto;
	max-width: 90%;

	&__txt--title {
		font-family: 'Sacramento', cursive;
		color: $primaryColor;
		font-size: 8rem;
		font-weight: 400;
	}

	&__txt--heading {
		margin-block: 0 $tiny;
		// margin-left: $medium;
	}

	&__main {
		padding-top: 0;
		margin-top: 0;
	}

	&__txt {
		align-self: flex-start;
	}

	&__img {
		margin-top: -$large;
	}
}

// =================== PARTNERS SECTION ===============
.partners {
	width: 90%;
	z-index: 2;
	margin-bottom: $tiny;

	// the section wrapper with the bg
	&__wrapper {
		height: calc($large * 4);
		background-image: url('/assets/images/partners-bg.svg');
		clip-path: polygon(0 43%, 100% 0, 100% 100%, 0% 100%);
		background-size: cover;
		background-repeat: no-repeat;
		display: flex;
		align-items: flex-end;
		justify-content: center;
		position: relative;

		&::after {
			content: '';
			display: block;
			height: 100%;
			inset: 0 0 auto;
			z-index: 1;
			position: absolute;
			clip-path: polygon(0 43%, 100% 0, 100% 100%, 0% 100%);
			background-color: rgba($primaryColor, 0.8);
		}
	}

	.col-3 {
		grid-gap: $medium;
	}

	&__heading {
		text-align: center;
		margin-bottom: $small;
	}

	&__card {
		background: linear-gradient(
			222.92deg,
			rgba(255, 255, 255, 0.16) 0%,
			rgba(248, 248, 248, 0) 100%
		);
		box-shadow: -15px 17px 27px 1px rgba(13, 13, 13, 0.15);

		&--heading {
			color: $backgroundColor;
		}
	}

	.icon {
		height: $small;
		margin-left: 1rem;
	}
}

// ================= UPCOMING EVENTS SECTION ===============
.events {
	width: 80%;
	margin-block: $big 14rem;
	margin-inline: auto;

	@include desktop-display;

	&__txt {
		display: flex;
		justify-content: space-between;
		align-items: baseline;
		width: 100%;
		// padding-inline: $tiny;
		margin-block: $small;
	}

	&__heading {
		font-size: 2rem;
		margin: 0 auto 0 0;
	}

	.card {
		padding: 0;
	}

	.event {
		flex-direction: column;

		&__img {
			width: 100%;
			// height: 15rem;
		}

		&__txt {
			width: 100%;
		}

		&__date {
			display: flex;

			.icon {
				margin-right: 0.5rem;
			}
		}
	}

	&__btn {
		margin: $small auto;
	}
}

//============= FATHER'S BLESSING (PROPHECY) =======================
.prophecy-section.split-2 {
	background-color: $secondaryColor;
	color: $backgroundColor;
	margin: $big 0;
	height: 40rem;
	padding-right: $small;
	width: 100%;
	position: relative;

	.main-img {
		position: absolute;
		width: 36%;
		bottom: 0;
		left: $tiny;
	}
}

// ==================== ABOUT ============
.about {
	margin-bottom: $medium;
	align-items: center;
	margin-inline: auto;
	max-width: 90%;

	.tertiary-heading {
		margin-bottom: -$tiny;
		width: 50%;
	}

	&__main {
		margin-top: 0;
		padding-top: 0;
	}

	&__txt {
		width: 70%;
	}

	&__img {
		width: 25%;
		box-shadow: 2px 2px 5px 0px rgba(0, 0, 0, 0.5);
	}
}

// ===================== TESTIMONIES ====================
.testimonies {
	width: 100%;
	text-align: center;
	padding: $small;
	margin-bottom: $medium;
	margin-inline: auto;
	position: relative;
	overflow: hidden;

	&::after {
		display: block;
		content: url('/assets/images/map-bg.png');
		width: 100%;
		top: $tiny;
		bottom: $tiny;
		position: absolute;
		z-index: -1;
		isolation: isolate;
		animation: loopHorizontal 15s linear infinite;
	}

	.swiper {
		width: 100%;
	}

	// MAIN SWIPER CONTENT WRAPPER
	.slide-content {
		max-width: 112rem;
		margin-inline: auto;
		width: 100%;
		padding: $small 0;
		overflow: hidden;
	}

	&__heading {
		font-size: 2.5rem;
	}

	&__main {
		margin-block: $small;
	}

	&__card {
		display: flex;
		flex-direction: column;
		align-items: center;
		padding: $tiny;
		background-color: $backgroundColor;
		box-shadow: 2px 2px 10px 6px rgba(142, 142, 142, 0.15);
		border-radius: $tiny;

		&--name {
			margin-bottom: 0;
			font-weight: 500;
		}
	}

	// PAGINATION BULLETS
	.swiper-pagination-bullet {
		background-color: $primaryColor;
	}
}

// ============ CALL-T0-ACTION ==============
.cta {
	width: 100%;
	height: 80%;
	padding: $small $tiny;
	margin-top: auto;
	border-radius: 1rem;
	background-color: $brightOrange;
	color: $backgroundColor;
	display: flex;
	justify-content: space-between;
	position: relative;

	&::after {
		display: block;
		content: 'Be in the Know';
		font-family: 'Gabriela', serif;
		font-size: 5rem;
		color: transparent;
		-webkit-text-stroke: 2px rgba($backgroundColor, 0.4);
		top: $big;
		right: 25%;
		position: absolute;
		z-index: 1;
		// animation: loopHorizontal 15s linear infinite;
	}

	// CTA CONTAINER
	&__wrapper {
		width: 80%;
		height: calc($large * 3);
		position: relative;
		display: flex;
		align-items: flex-end;
		margin: $small auto;
	}

	&__txt {
		width: 60%;
		display: flex;
		flex-direction: column;

		&--info {
			margin-block: $small 1rem;
		}
	}

	// CTA FORM
	&__form {
		width: 90%;
		display: flex;
		align-items: center;
		justify-content: space-between;
		background-color: #fff;
		border-radius: 1.5rem;
		padding: 0.5rem;

		&--input {
			border: unset;
			outline: unset;
			width: 70%;
			padding: 1rem;
		}

		.btn {
			border-radius: 1.5rem;
			background-color: rgba($paleOrange, 0.8);
		}

		&--btn,
		.icon {
			height: 100%;
		}
	}

	&__img {
		width: 25%;
		// position: absolute;
		right: $small;
		align-self: flex-end;
		z-index: 2;

		.main-img {
			width: 100%;
		}
	}
}

// ==================== MEDIA QUERIES ==================
@media screen and (max-width: 1024px) {
	.prophecy-section.split-2 {
		height: 25rem;
	}

	.about {
		.tertiary-heading {
			margin-bottom: 0;
		}
	}
}

@media screen and (max-width: 800px) {
	.welcome__img {
		width: 60%;
		order: -1;
		margin-top: 0;
	}

	// ============ UPCOMING EVENTS =======
	.events {
		@include mobile-display;

		&__txt {
			flex-direction: column;
			gap: $tiny;
			margin-bottom: $tiny;
		}
	}

	.about {
		.tertiary-heading {
			text-align: center;
			margin-bottom: $tiny;
			width: 100%;
		}

		&__txt,
		&__img {
			width: 80%;
			margin-inline: auto;
		}

		&__main {
			flex-direction: column-reverse;
		}
	}

	// ============= PARTNERS SECTION =====================
	.partners {
		width: 80%;

		&__wrapper {
			height: auto;
			padding-block: $small;
			clip-path: none;

			&::after {
				clip-path: none;
			}
		}
	}

	// =============== FATHER'S BLESSING ==============
	.prophecy-section.split-2 {
		flex-direction: column;
		height: unset;

		.main-img {
			margin-top: -12rem;
			position: static;
			width: 80%;
		}

		.prophecy-section__txt {
			padding: $tiny;
		}
	}

	// =================== CTA ====================

	// CTA CONTAINER
	.cta__wrapper {
		height: auto;
		align-items: flex-end;
	}

	.cta {
		padding: $tiny;
		justify-content: space-between;

		&__txt {
			width: 70%;

			&--info {
				margin-block: 1rem;
				line-height: 1;
				font-size: 1.3rem;
			}
		}

		// CTA FORM
		&__form {
			width: 100%;
			border-radius: 1.5rem;
			padding: 0.5rem;

			&--input {
				width: 70%;
				padding: 1rem;
			}

			.btn {
				border-radius: 1.5rem;
			}
		}

		&__img {
			display: none;
		}

		&::after {
			right: 0;
			top: $small;
		}
	}
}

@media screen and (max-width: 450px) {
	.hero {
		text-align: center;
	}

	.welcome {
		.welcome__txt--title,
		.welcome__txt--heading {
			margin-inline: $small;
		}

		.welcome__main {
			margin-top: $small;
		}
	}

	.about {
		.tertiary-heading {
			margin-inline: $small;
			margin-bottom: $small;
			width: inherit;
		}

		&__main {
			padding: 0;
		}
	}

	.cta::after {
		display: none;
	}

	.cta__txt {
		width: 100%;
	}
}

sources.assets\\sass\\layouts\\modal.scss:
@use '../abstracts/variables' as *;

.backdrop {
	width: 100%;
	height: 100vh;
	height: 100svh;
	display: flex;
	justify-content: center;
	align-items: center;
	// position: relative;
	background-color: #e6e4e4;
}

.modal {
	width: 40%;
	min-width: 34rem;
	padding: $medium $tiny;
	border-radius: $tiny;
	background-color: $backgroundColor;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;
	box-shadow: 2px 2px 10px 6px rgba(142, 142, 142, 0.15);
	position: relative;

	&__close {
		position: absolute;
		right: $tiny;
		top: $tiny;
		cursor: pointer;

		.icon {
			width: $tiny;
		}
	}

	&__img {
		width: $small;
	}

	.secondary-heading {
		font-size: 2.5rem;
		margin-top: $tiny;
	}

	&__txt {
		text-align: center;
		margin-block: $tiny;
	}

	// FORM
	.form__item {
		width: 100%;
		height: 5rem;
		display: flex;
		padding-inline: 1rem;
		gap: 0.5rem;
		align-items: center;
		outline: 2px solid rgba($accentColor, 0.2);
		background-color: rgba($backgroundColor, 0.8);
		border-radius: 0.5rem;
		margin-bottom: $tiny;
	}

	.form__input,
	.form__input:focus {
		width: 100%;
		height: 100%;
		border: unset;
		outline: unset;
		background-color: transparent;
		padding: 1rem;
	}

	.pswd-icon {
		cursor: pointer;
	}

	.submit-btn {
		width: 80%;
	}
}

// ================ DONATION MODAL ===========
// OUTER WRAPPER
.donation__wrapper {
	position: fixed;
	z-index: 20;
	height: 100vh;
	height: 100svh;
	top: $tiny;
	bottom: 0;
	left: 0;
	right: 0;
	border: unset;
	background-color: rgba($accentColor, 0.2);
	animation-name: popIn;
	animation-duration: 1s;
	animation-timing-function: ease-in-out;

	// INNER CONTAINER (CONTENT WRAPPER)
	.donation__container {
		max-width: 85%;
		min-width: 40%;
		min-width: 34rem;
		height: 45rem;
		padding: 0;
		border-radius: $tiny;
		background-image: url('/assets/images/partners-bg.svg');
		background-size: cover;
		background-repeat: no-repeat;
		background-position: left;
		background-color: rgba($primaryColor, 1);
		position: relative;

		&::before {
			content: '';
			display: block;
			height: 100%;
			z-index: 1;
			position: absolute;
			background-color: $primaryColor;
		}
	}

	// MODAL (MAIN CONTENT)
	.donation {
		background-color: rgba($primaryColor, 0.8);
		width: 100%;
		height: 100%;
		padding: $small;
		border-radius: $tiny;

		// CARD GROUP
		&__group {
			display: flex;
			align-items: center;
			justify-content: space-between;
			gap: $small;
			// flex-wrap: wrap;
		}
		// DONATION CARD
		&__card {
			margin-top: $tiny;
			width: 27rem;
			height: 25rem;
			background: linear-gradient(
				222.92deg,
				rgba(255, 255, 255, 0.16) 0%,
				rgba(248, 248, 248, 0.1) 100%
			);
			box-shadow: -15px 17px 27px 1px rgba(13, 13, 13, 0.15);
			transition: all ease-in-out 0.2s;

			&--heading {
				color: $backgroundColor;
			}
		}

		&__card:hover {
			scale: 1.05;
		}

		&__card:active {
			transform: translateY(2rem);
		}

		.icon {
			height: $small;
			margin-left: 1rem;
		}
	}

	// ================= MEDIA QUERIES =================
	@media screen and (max-width: 650px) {
		height: 80vh;
		height: 80svh;
		top: unset;

		.donation__container {
			height: auto;
			margin-top: $small;
		}

		.donation {
			&__group {
				flex-direction: column;
				gap: 0;
			}

			&__card {
				height: fit-content;
			}
		}
	}
}

sources.assets\\sass\\layouts\\_partners.scss:
// STYLES FOR THE PARTNERS PAGE
@use '../abstracts/variables' as *;

.partners-page {
	.header {
		background-image: url('/assets/images/partner-page-bg.png');
		background-color: rgb(255, 255, 255, 0.1);

		&__wrapper {
			padding-top: 0;
			background-image: unset;
			background-color: rgb(236, 153, 47, 0.1);
			color: black;
			// background-blend-mode: normal;
		}

		.hero {
			display: flex;
			justify-content: center;
			align-items: center;
			// width: 80%;
			margin: auto;
			height: 100%;
		}
	}

	.description {
		margin: $small;
		text-align: center;
	}

	.partners__container {
		background-color: #1e293b;
		padding-block: $small;
		width: 100%;
		margin: 0;
	}

	.partners {
		width: 80%;
		margin: $small auto;
		grid-template-columns: repeat(auto-fit, minmax(18rem, 1fr));
		gap: $small;

		&__logo {
			width: 80%;
		}

		&__heading {
			font-family: 'Gabriela', serif;
			font-size: 6rem;
			font-weight: normal;
			color: transparent;
			-webkit-text-stroke: 2px rgba($backgroundColor, 0.8);
		}

		&__card {
			background-image: linear-gradient(
				222.92deg,
				rgba(255, 255, 255, 0.16) 0%,
				rgba(248, 248, 248, 0) 100%
			);
			height: fit-content;
			border-radius: 1rem;
			border-top: 2px solid $backgroundColor;
			padding: $tiny;

			&--amount {
				color: $backgroundColor;
				margin-block: $tiny;
			}

			&--feature {
				font-size: 1.4rem;
				width: 100%;
				display: flex;
				align-items: center;
			}

			&--feature:not(:last-of-type) {
				border-bottom: 2px solid rgba($backgroundColor, 0.4);
				margin-bottom: 1rem;
			}

			.icon {
				width: 1.6rem;
				margin-right: 1rem;
			}

			.signup-link {
				color: $primaryColor;
				text-transform: capitalize;
				font-size: 2rem;
				display: inline-block;
				margin-top: $tiny;
			}
		}

		&__card:nth-child(2) {
			border: 2px solid $backgroundColor;
		}
	}
}

sources.assets\\sass\\layouts\\rigx.scss:
// =============== RIGX PAGE STYLES ====================
@use '../abstracts/variables' as *;

.rigx-page {
	background-color: rgba($accentColor, 0.2);

	.header {
		background-image: url('/assets/images/rigx-bg.png');
		max-height: 56rem;
	}

	.hero {
		width: 80%;
		margin: auto;
		display: flex;
		flex-direction: column;
		align-items: center;
		justify-content: center;
	}

	.main {
		margin-block: $small;
	}

	.contact-wrapper {
		margin: 0 $small;

		hr {
			border: 0.5px solid rgba($accentColor, 0.4);
			margin-bottom: 1px;
		}

		.contact-heading {
			display: flex;
			align-items: center;
			margin-top: $tiny;
		}
	}

	.contact__group {
		margin: $tiny auto;
		grid-template-columns: repeat(auto-fit, minmax(20rem, 21rem));
		gap: $tiny;

		.contact {
			font-size: 1.4rem;
			min-height: 23rem;
			padding: 1rem;
			background-color: $backgroundColor;
			display: flex;
			flex-direction: column;
			gap: 1rem;

			&__name {
				font-weight: bold;
			}

			&__email {
				text-decoration: underline;
			}
		}
	}

	// =============== MEDIA QUERIES ===================

	@media screen and (max-width: 650px) {
		.contact__group {
			grid-template-columns: 1fr;
		}
	}
}

sources.assets\\sass\\layouts\\shop.scss:
// ============ SHOP PAGE STYLE =====================
@use '../abstracts/variables' as *;

.featured {
	height: 45rem;
	width: 100%;
	position: relative;
	margin-bottom: $tiny;

	&__img {
		width: 100%;
		height: 100%;
		object-fit: fill;
		-o-object-fit: fill;
	}

	&__card {
		background-color: #2f1f09;
		color: rgba($backgroundColor, 1);
		width: fit-content;
		display: flex;
		flex-direction: column;
		align-items: center;
		justify-content: center;
		gap: 1rem;
		position: absolute;
		z-index: 5;
		top: 50%;
		left: 50%;
		transform: translate(-50%, -50%);

		.paragraph {
			font-size: 1.2rem;
			margin: 0;
			padding: 0;
		}

		&--title {
			text-decoration: underline;
		}

		&--btn-group {
			display: flex;
			flex-wrap: wrap;
			gap: $tiny;
		}

		.primary-heading {
			text-transform: uppercase;
			margin: $tiny 0;
			padding: 0;
		}

		.btn {
			text-transform: uppercase;
			padding: 1rem $tiny;
			transition: all cubic-bezier(0.215, 0.61, 0.355, 1) 0.2s;
		}

		.btn:hover {
			color: rgba($primaryColor, 1);
		}
	}
}

.shop-page {
	background-color: rgba($accentColor, 0.2);

	.navbar {
		background-color: rgba($accentColor, 0.6);
		color: $backgroundColor;
		top: 0;
	}

	.top-bar {
		background-color: transparent;
	}

	.search-form__label {
		background-color: rgba($backgroundColor, 0.8);
	}

	.search-form__close {
		color: rgba($accentColor, 0.6);
	}

	.main {
		margin-inline: auto;
		width: 80%;
		padding-top: 12rem;
	}

	.shop-category,
	.shop-category:focus {
		border: unset;
		outline: 2px solid rgba($primaryColor, 1);
		padding: 1rem;
		// width: $;
		border-radius: 0.5rem;
		background-color: rgba($backgroundColor, 0.8);
	}

	.merch-group {
		grid-template-columns: repeat(auto-fit, minmax(18rem, 1fr));

		.btn {
			font-size: 1.6rem;
			padding-inline: 0.5rem;
		}
	}

	.shop {
		width: 100%;
		text-align: center;
		padding: $tiny;
		margin-bottom: $medium;

		&__heading {
			font-size: 2.5rem;
		}

		&__card {
			display: flex;
			flex-direction: column;
			gap: 1rem;
			padding: $tiny;
			background-color: $backgroundColor;
			box-shadow: 2px 2px 10px 6px rgba(142, 142, 142, 0.15);
			border-radius: 0.5rem;
			text-align: left;

			&--heading {
				margin-bottom: 0;
				// font-weight: 500;
			}

			&--img {
				width: 100%;
			}

			&--info {
				font-size: 1.4rem;

				// truncate word length
				overflow: hidden;
				text-overflow: ellipsis;
				display: -webkit-box;
				-webkit-line-clamp: 5;
				line-clamp: 5;
				-webkit-box-orient: vertical;
			}
		}
	}

	.card-price {
		font-size: 1.8rem;
		font-weight: bold;
	}

	.card-footer {
		width: 100%;
		display: flex;
		gap: 1rem;
		justify-content: space-between;
		align-items: center;
		margin-top: auto;

		.icon {
			margin-right: 0.5rem;
		}
	}

	.main-btn {
		text-transform: uppercase;
		margin: $small auto;
		width: 50%;
	}
}

.mini-card-section {
	grid-template-columns: repeat(auto-fit, minmax(15rem, 1fr));

	.shop__card {
		padding: 1.5rem;

		.card-price {
			font-size: 1.2rem;
		}
	}

	.btn {
		font-size: 1.2rem;
		padding: 0.5rem;

		.icon {
			height: 1.5rem;
		}
	}
}

.mini-card-heading {
	font-size: 1.5rem;
	font-weight: 700;
}

// =============== MEDIA QUERIES ========================
@media screen and (max-width: 800px) {
	.secondary-heading,
	.primary-heading {
		margin: 0;
	}

	.top-bar {
		padding: 0;
	}

	.featured {
		display: flex;
		flex-direction: column;
		height: auto;

		&__img {
			height: 50%;
		}

		&__card {
			width: 100%;
			position: unset;
			transform: unset;
		}
	}

	.shop-page {
		.shop {
			padding: 0;
			margin-top: $tiny;
		}

		.search-form {
			position: absolute;
			right: $small;
			top: $large;
			z-index: 5;

			.icon {
				display: none;
			}

			.main-btn {
				font-size: 1.8rem;
			}
		}
	}
}

@media screen and (max-width: 450px) {
	.featured {
		.primary-heading {
			font-size: 2rem;
		}
	}

	.featured__card--btn-group {
		flex-direction: column;
	}

	.mini-card-heading {
		font-size: 1.2rem;
		font-weight: 400;
	}

	.mini-card-section {
		.card-price {
			font-size: 1.3rem;
		}
	}
}

@media screen and (max-width: 375px) {
	.featured {
		.primary-heading {
			font-size: 1.8rem;
		}
	}

	.mini-card-section {
		grid-template-columns: 1fr;
	}
}
